body.script:
$('.marquee').marquee({
    speed: 50,
    gap: 100,
    delayBeforeStart: 0,
    direction: 'left',
    duplicated: true,
    pauseOnHover: true
});
$(function () {
    const modal = document.querySelector('.modal')
    closeHandlers = document.querySelectorAll('.close-modal')

    const hideModal = Cookie.get('hide_modal')

    if (!hideModal) {
        // openModal()
    }

    closeHandlers.forEach(closeHandler => {
        closeHandler.addEventListener('click', closeModal)
    });
    function openModal() {
        modal.classList.add('visible');
    }
    function closeModal() {
        modal.classList.remove('visible');
        Cookie.set('hide_modal', true, { expires: 1 })
    }

    $('img').Lazy();
})
